import "./Cell_styles.scss";

import React, { useState } from "react";
import sfx from "../../Assets/Sounds/shatter.mp3";

const Cell = ({ cellClass }) => {
  const [checked, setChecked] = useState(false);
  const [broken, setBroken] = useState("");
  const [text, setText] = useState("This is the Cell txt");

  const playSound = () => {
    let audio = new Audio("../../Assets/Sounds/shatter.mp3");
    sfx.play();
  };

  return (
    <div
      className={`${cellClass} ${broken}`}
      onClick={() => {
        if (checked === false) {
          setChecked(true);
          playSound();
          const brokenTextureNumber =
            Math.floor(Math.random() * (2 - 1 + 1)) + 1;
          setBroken(`broken${brokenTextureNumber}`);
        } else {
          return;
        }
      }}
    >
      <p className="cell_text">{text}</p>
    </div>
  );
};

export default Cell;
