import "./Cell_styles.scss";

import React, { useState, useRef, useEffect } from "react";

const Button = ({ cellClass }) => {
  const [checked, setChecked] = useState(false);
  const [text, setText] = useState("This is the Cell txt");

  return (
    <div
      className={cellClass}
      onClick={() => {
        if (checked === false) {
          setChecked(true);
          setText("I was checked");
        } else {
          return;
        }
      }}
    >
      <p className="cell_text">{text}</p>
    </div>
  );
};

export default Button;
