import "./Cell_styles.scss";
import VanillaTilt from "vanilla-tilt";
import React, { useState, useRef, useEffect } from "react";

const Cell = ({ cellClass }, props) => {
  const [checked, setChecked] = useState(false);
  const [text, setText] = useState("This is the Cell txt");
  const { options, ...rest } = props;

  const tilt = useRef(null);

  // const options = {
  //   glare: true,
  //   "max-glare": 0.7,
  //   scale: 2,
  // };

  useEffect(() => {
    VanillaTilt.init(tilt.current, options);
  }, [options]);

  return (
    <div ref={tilt} {...rest}>
      <div
        className={cellClass}
        onClick={() => {
          if (checked === false) {
            setChecked(true);
            setText("I was checked");
          } else {
            return;
          }
        }}
      >
        <p className="cell_text">{text}</p>
      </div>
    </div>
  );
};

export default Cell;
